```javascript
class Product {
  constructor(id, name, price, category) {
    this.id = id;
    this.name = name;
    this.price = price;
    this.category = category;
  }

  applyDiscount(percent) {
    this.price *= (1 - percent / 100);
    return this;
  }

  displayInfo() {
    return `${this.name} - $${this.price.toFixed(2)} (${this.category})`;
  }
}

class ShoppingCart {
  constructor() {
    this.items = [];
    this.#initialize();
  }

  #initialize() {
    console.log("Shopping cart initialized");
  }

  addItem(product, quantity = 1) {
    const existingItem = this.items.find(item => item.product.id === product.id);

    if (existingItem) {
      existingItem.quantity += quantity;
    } else {
      this.items.push({ product, quantity });
    }
  }

  removeItem(productId) {
    this.items = this.items.filter(item => item.product.id !== productId);
  }

  getTotal() {
    return this.items.reduce((total, item) => {
      return total + (item.product.price * item.quantity);
    }, 0);
  }

  displayCart() {
    if (this.items.length === 0) {
      return "Cart is empty";
    }

    return this.items.map(item => 
      `${item.product.name} x${item.quantity} - $${(item.product.price * item.quantity).toFixed(2)}`
    ).join('\n') + `\nTotal: $${this.getTotal().toFixed(2)}`;
  }

  static createFromProductList(products) {
    const cart = new ShoppingCart();
    products.forEach(product => cart.addItem(product));
    return cart;
  }
}

// Menggunakan classes
const laptop = new Product(1, "Laptop", 1000, "Electronics");
const phone = new Product(2, "Phone", 500, "Electronics");

const cart = new ShoppingCart();
cart.addItem(laptop);
cart.addItem(phone, 2);

console.log(cart.displayCart());
console.log("Total:", cart.getTotal()); // 2000
```

