```javascript
class InteractiveForm {
  constructor(formId) {
    this.form = document.getElementById(formId);
    this.inputs = {};
    this.errors = {};
    this.init();
  }

  init() {
    // Real-time validation
    this.form.addEventListener('input', (event) => {
      this.validateField(event.target);
    });

    // Form submission
    this.form.addEventListener('submit', (event) => {
      event.preventDefault();
      if (this.validateForm()) {
        this.handleSubmit();
      }
    });

    // Keyboard shortcuts
    document.addEventListener('keydown', (event) => {
      if (event.ctrlKey && event.key === 'Enter') {
        this.form.dispatchEvent(new Event('submit'));
      }
    });
  }

  validateField(field) {
    const value = field.value.trim();
    const fieldName = field.name;

    // Clear previous error
    this.clearError(fieldName);

    // Validation rules
    if (field.required && !value) {
      this.showError(fieldName, 'This field is required');
      return false;
    }

    if (field.type === 'email' && value) {
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(value)) {
        this.showError(fieldName, 'Please enter a valid email');
        return false;
      }
    }

    if (fieldName === 'password' && value.length < 8) {
      this.showError(fieldName, 'Password must be at least 8 characters');
      return false;
    }

    return true;
  }

  validateForm() {
    let isValid = true;
    const fields = this.form.querySelectorAll('[name]');

    fields.forEach(field => {
      if (!this.validateField(field)) {
        isValid = false;
      }
    });

    return isValid;
  }

  showError(fieldName, message) {
    this.errors[fieldName] = message;

    const field = this.form.querySelector(`[name="${fieldName}"]`);
    field.classList.add('error');

    let errorElement = field.parentElement.querySelector('.error-message');
    if (!errorElement) {
      errorElement = document.createElement('div');
      errorElement.className = 'error-message';
      field.parentElement.appendChild(errorElement);
    }

    errorElement.textContent = message;
  }

  clearError(fieldName) {
    const field = this.form.querySelector(`[name="${fieldName}"]`);
    if (field) {
      field.classList.remove('error');
      const errorElement = field.parentElement.querySelector('.error-message');
      if (errorElement) {
        errorElement.remove();
      }
    }
    delete this.errors[fieldName];
  }

  handleSubmit() {
    const formData = new FormData(this.form);
    const data = Object.fromEntries(formData);

    console.log('Form submitted:', data);
    alert('Form submitted successfully!');
    this.form.reset();
  }
}

// Initialize form
const myForm = new InteractiveForm('signup-form');
```

