      const data = await this.apiClient.get(
        `/weather?q=${city}&appid=${this.apiKey}&units=metric`
      );

      return this.formatWeatherData(data);
    } catch (error) {
      throw new Error(`Failed to get weather for ${city}: ${error.message}`);
    }
  }

  async getForecast(city) {
    try {
      const data = await this.apiClient.get(
        `/forecast?q=${city}&appid=${this.apiKey}&units=metric`
      );

      return data.list.slice(0, 5).map(item => this.formatWeatherData(item));
    } catch (error) {
      throw new Error(`Failed to get forecast for ${city}: ${error.message}`);
    }
  }

  formatWeatherData(data) {
    return {
      temperature: Math.round(data.main.temp),
      description: data.weather[0].description,
      humidity: data.main.humidity,
      windSpeed: data.wind.speed,
      city: data.name,
      icon: data.weather[0].icon
    };
  }
}

