// main.js
import { TaskManager } from './modules/taskManager.js';
import { UIManager } from './modules/uiManager.js';
import { ApiService } from './modules/apiService.js';

class TaskMasterApp {
  constructor() {
    this.taskManager = new TaskManager();
    this.uiManager = new UIManager('app-container');
    this.apiService = new ApiService('https://api.example.com');

    this.isOnline = navigator.onLine;
    this.init();
  }

  async init() {
    // Load initial data
    await this.loadInitialData();

    // Setup event listeners
    this.setupEventListeners();

    // Setup offline detection
    this.setupOfflineSupport();

    // Initial render
    this.render();
  }

  async loadInitialData() {
    try {
      // Try to load from remote API first
      if (this.isOnline) {
        const remoteTasks = await this.apiService.getRemoteTasks();
        this.taskManager.tasks = remoteTasks;
      } else {
        // Fallback to local storage
        await this.taskManager.loadTasks();
      }
    } catch (error) {
      console.error('Failed to load initial data:', error);
      await this.taskManager.loadTasks(); // Load from localStorage
    }
  }

  setupEventListeners() {
    // Form submission
    document.getElementById('task-form').addEventListener('submit', (e) => {
      e.preventDefault();
      this.handleAddTask();
    });

    // Filter changes
    document.getElementById('filter-select').addEventListener('change', (e) => {
      this.filters.status = e.target.value;
      this.render();
    });

    // Search input
    document.getElementById('search-input').addEventListener('input', (e) => {
      this.filters.search = e.target.value;
      this.render();
    });

    // Keyboard shortcuts
    document.addEventListener('keydown', (e) => {
      if (e.ctrlKey && e.key === 'k') {
        e.preventDefault();
        document.getElementById('search-input').focus();
      }
    });
  }

  setupOfflineSupport() {
    window.addEventListener('online', () => {
      this.isOnline = true;
      this.uiManager.showNotification('Back online! Syncing data...', 'success');
      this.syncData();
    });

    window.addEventListener('offline', () => {
      this.isOnline = false;
      this.uiManager.showNotification('You are offline', 'warning');
    });
  }

  async handleAddTask() {
    const form = document.getElementById('task-form');
    const formData = new FormData(form);

    const taskData = {
      title: formData.get('title').trim(),
      description: formData.get('description').trim(),
      priority: formData.get('priority') || 'medium',
      dueDate: formData.get('dueDate'),
      createdAt: new Date().toISOString()
    };

    if (!taskData.title) {
      this.uiManager.showNotification('Task title is required', 'error');
      return;
    }

    try {
      const newTask = this.taskManager.addTask(taskData);

      // Sync with remote if online
      if (this.isOnline) {
        await this.apiService.syncTasks(this.taskManager.tasks);
      }

      this.uiManager.showNotification('Task added successfully!', 'success');
      form.reset();
      this.render();

    } catch (error) {
      this.uiManager.showNotification('Failed to add task', 'error');
      console.error('Add task error:', error);
    }
  }

  async handleToggleTask(taskId) {
    try {
      this.taskManager.toggleTask(taskId);

      if (this.isOnline) {
        await this.apiService.syncTasks(this.taskManager.tasks);
      }

      this.render();
    } catch (error) {
      this.uiManager.showNotification('Failed to update task', 'error');
    }
  }

  async handleDeleteTask(taskId) {
    if (!confirm('Are you sure you want to delete this task?')) return;

    try {
      this.taskManager.deleteTask(taskId);

      if (this.isOnline) {
        await this.apiService.syncTasks(this.taskManager.tasks);
      }

      this.uiManager.showNotification('Task deleted', 'info');
      this.render();
    } catch (error) {
      this.uiManager.showNotification('Failed to delete task', 'error');
    }
  }

  async syncData() {
    if (!this.isOnline) return;

    try {
      await this.apiService.syncTasks(this.taskManager.tasks);
      this.uiManager.showNotification('Data synced successfully!', 'success');
    } catch (error) {
      console.error('Sync failed:', error);
      this.uiManager.showNotification('Sync failed', 'error');
    }
  }

  render() {
    const filteredTasks = this.taskManager.filterTasks(this.filters);
    this.uiManager.renderTaskList(filteredTasks);

    // Update stats
    this.updateStats();
  }

  updateStats() {
    const stats = this.taskManager.getStats();

    document.getElementById('total-tasks').textContent = stats.total;
    document.getElementById('completed-tasks').textContent = stats.completed;
    document.getElementById('pending-tasks').textContent = stats.pending;
  }
}

// Initialize app
document.addEventListener('DOMContentLoaded', () => {
  new TaskMasterApp();
});
