// Final project challenge - Implement complete task manager
class TaskManagerApp {
  constructor() {
    this.tasks = [];
    this.filters = { status: 'all', search: '' };
    this.init();
  }

  async init() {
    await this.loadTasks();
    console.log('Task Manager initialized!');
    this.render();
  }

  async loadTasks() {
    const stored = localStorage.getItem('tasks');
    this.tasks = stored ? JSON.parse(stored) : [];
  }

  async saveTasks() {
    localStorage.setItem('tasks', JSON.stringify(this.tasks));
  }

  addTask(title) {
    const task = {
      id: Date.now(),
      title,
      completed: false,
      createdAt: new Date().toISOString()
    };
    this.tasks.push(task);
    this.saveTasks();
    console.log(`Task added: ${title}`);
    this.render();
  }

  toggleTask(id) {
    const task = this.tasks.find(t => t.id === id);
    if (task) {
      task.completed = !task.completed;
      this.saveTasks();
      console.log(`Task ${task.completed ? 'completed' : 'uncompleted'}: ${task.title}`);
      this.render();
    }
  }

  deleteTask(id) {
    const taskIndex = this.tasks.findIndex(t => t.id === id);
    if (taskIndex !== -1) {
      const task = this.tasks[taskIndex];
      this.tasks.splice(taskIndex, 1);
      this.saveTasks();
      console.log(`Task deleted: ${task.title}`);
      this.render();
    }
  }

  render() {
    const filtered = this.tasks.filter(task => {
      const matchesStatus = this.filters.status === 'all' || 
        (this.filters.status === 'active' && !task.completed) ||
        (this.filters.status === 'completed' && task.completed);

      const matchesSearch = task.title.toLowerCase()
        .includes(this.filters.search.toLowerCase());

      return matchesStatus && matchesSearch;
    });

    console.log('Current tasks:', filtered);
  }
}

